const teamMembers = [
  {
    name: "Avery Chan",
    position: "Managing Partner",
    description: "Avery leads our firm with over 15 years of experience in tax strategy, audit, and financial planning.",
    image: "https://images.unsplash.com/photo-1607746882042-944635dfe10e?auto=format&fit=crop&w=600&q=80"
  },
  {
    name: "Leo Ramirez",
    position: "Tax Compliance Specialist",
    description: "Leo ensures our clients meet all tax obligations while maximizing savings opportunities.",
    image: "https://images.unsplash.com/photo-1603415526960-f7e0328e8328?auto=format&fit=crop&w=600&q=80"
  },
  {
    name: "Isabelle Cruz",
    position: "Accounting Supervisor",
    description: "With a focus on integrity and accuracy, Isabelle oversees all bookkeeping and accounting operations.",
    image: "https://images.unsplash.com/photo-1522071820081-009f0129c71c?auto=format&fit=crop&w=600&q=80"
  }
];

export default function Team() {
  return (
    <section className="py-20 px-6 bg-gray-50">
      <h2 className="text-4xl font-bold text-center mb-16">Meet Our Team</h2>
      <div className="space-y-12 max-w-6xl mx-auto">
        {teamMembers.map((member, i) => (
          <div
            key={i}
            className="flex flex-col md:flex-row items-center bg-white shadow-lg rounded-2xl overflow-hidden"
          >
            {/* Image Section */}
            <img
              src={member.image}
              alt={member.name}
              className="w-full md:w-1/2 h-64 md:h-full object-cover"
            />

            {/* Text Section */}
            <div className="p-8 md:w-1/2 text-center md:text-left">
              <h3 className="text-2xl font-bold mb-2">{member.name}</h3>
              <p className="text-cyan-700 font-medium mb-4">{member.position}</p>
              <p className="text-gray-600">{member.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
