const teamMembers = [
  {
    name: "Avery Chan",
    position: "Managing Partner",
    description:
      "Avery leads our firm with over 15 years of experience in tax strategy, audit, and financial planning.",
    image:
      "https://images.unsplash.com/photo-1607746882042-944635dfe10e?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Leo Ramirez",
    position: "Tax Compliance Specialist",
    description:
      "Leo ensures our clients meet all tax obligations while maximizing savings opportunities.",
    image:
      "https://images.unsplash.com/photo-1603415526960-f7e0328e8328?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Isabelle Cruz",
    position: "Accounting Supervisor",
    description:
      "With a focus on integrity and accuracy, Isabelle oversees all bookkeeping and accounting operations.",
    image:
      "https://images.unsplash.com/photo-1522071820081-009f0129c71c?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Nathan Yu",
    position: "Financial Analyst",
    description:
      "Nathan specializes in helping clients interpret financial data and make informed business decisions.",
    image:
      "https://images.unsplash.com/photo-1603415527081-26d5b6bbf5a9?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Mei Tan",
    position: "Senior Auditor",
    description:
      "Mei conducts in-depth audits and ensures that client records are accurate and compliant.",
    image:
      "https://images.unsplash.com/photo-1524504388940-b1c1722653e1?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Carlos Rivera",
    position: "Business Development Lead",
    description:
      "Carlos drives growth by building client relationships and identifying new market opportunities.",
    image:
      "https://images.unsplash.com/photo-1614283327064-3a2e417f38af?auto=format&fit=crop&w=600&q=80",
  },
];

export default function Team() {
  return (
    <section className="py-20 px-4 bg-gray-50">
      <h2 className="text-4xl font-bold text-center mb-16 text-cyan-700">Meet Our Leadership</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10 max-w-7xl mx-auto">
        {teamMembers.map((member, i) => (
          <div
            key={i}
            className="group relative bg-white shadow-xl rounded-xl overflow-hidden transition-transform transform hover:scale-105 hover:shadow-2xl"
          >
            <div className="aspect-w-3 aspect-h-4">
              <img
                src={member.image}
                alt={member.name}
                className="object-cover w-full h-full group-hover:brightness-90 transition duration-300"
              />
            </div>
            <div className="absolute bottom-0 bg-white bg-opacity-90 w-full p-4 text-center">
              <h3 className="text-xl font-bold text-cyan-700">{member.name}</h3>
              <p className="text-gray-600 text-sm">{member.position}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
