const services = [
  {
    title: "Tax Planning & Preparation",
    description: "Comprehensive, up-to-date strategies to minimize your federal and state tax liability while maintaining compliance."
  },
  {
    title: "Business Accounting",
    description: "Ongoing bookkeeping, payroll, and GAAP-compliant financial reporting for small businesses and corporations."
  },
  {
    title: "Consulting & Advisory",
    description: "Guidance on business formation, strategic growth, financial analysis, and risk management by CPAs."
  }
];

export default function Services() {
  return (
    <section className="py-20 bg-white px-6">
      <h2 className="text-3xl font-bold text-center mb-12">Our Services</h2>
      <div className="flex flex-col md:flex-row gap-8 justify-center">
        {services.map((service, i) => (
          <div key={i} className="bg-gray-100 rounded-lg p-6 shadow-md max-w-sm">
            <h3 className="text-lg font-semibold mb-2">{service.title}</h3>
            <p>{service.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
