export default function Hero() {
  return (
    <section className="bg-gray-600 text-white text-center py-20 px-4">
      <h1 className="text-4xl font-bold mb-4">Trust, Expertise, & Results.</h1>
      <p className="text-xl mb-6">Your Partner for Tax, Accounting & Consulting.</p>
      <p className="mb-8 max-w-xl mx-auto">
        Polarix Solutions empowers individuals and businesses to achieve financial clarity and confidence with premier CPA services delivered by experienced professionals.
      </p>
      <button className="bg-white text-blue-700 px-6 py-2 rounded-full font-semibold">
        Schedule a Consultation
      </button>
    </section>
  );
}
