import { motion } from "framer-motion";
import {
  FaFileInvoiceDollar,
  FaBalanceScale,
  FaClipboardCheck,
  FaBookOpen,
  FaCalculator,
  FaChartLine,
  FaLightbulb
} from "react-icons/fa";

const services = [
  {
    title: "Tax Services",
    description:
      "Tailored tax services for individuals, businesses, and not-for-profits. We ensure full compliance and optimized outcomes.",
    icon: <FaFileInvoiceDollar className="text-4xl text-cyan-700" />,
    link: "/services/tax"
  },
  {
    title: "Audit & Attest",
    description:
      "Reliable audits for profit and not-for-profit organizations. We ensure financial accuracy and regulatory compliance.",
    icon: <FaBalanceScale className="text-4xl text-cyan-700" />,
    link: "/services/audit"
  },
  {
    title: "Compilations & AUP",
    description:
      "Professional compilations and agreed-upon procedures to deliver precise and trustworthy financial insights.",
    icon: <FaClipboardCheck className="text-4xl text-cyan-700" />,
    link: "/services/compilations"
  },
  {
    title: "Bookkeeping & Accounting",
    description:
      "Day-to-day bookkeeping and advanced accounting services. We specialize in QuickBooks and financial clarity.",
    icon: <FaBookOpen className="text-4xl text-cyan-700" />,
    link: "/services/bookkeeping"
  },
  {
    title: "Tax Planning & Preparation",
    description:
      "Modern tax strategies to reduce your liability and keep your filings compliant at all times.",
    icon: <FaCalculator className="text-4xl text-cyan-700" />,
    link: "/services/tax-planning"
  },
  {
    title: "Business Accounting",
    description:
      "Comprehensive accounting and payroll support for small businesses with GAAP-compliant reporting.",
    icon: <FaChartLine className="text-4xl text-cyan-700" />,
    link: "/services/business"
  },
  {
    title: "Consulting & Advisory",
    description:
      "Expert guidance in entity setup, scaling strategy, and risk management tailored for your business goals.",
    icon: <FaLightbulb className="text-4xl text-cyan-700" />,
    link: "/services/consulting"
  }
];

export default function Services() {
  return (
    <section className="py-20 bg-white px-6">
      <h2 className="text-3xl font-bold text-center mb-12">Our Services</h2>
      <div className="grid gap-10 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 max-w-7xl mx-auto">
        {services.map((service, i) => (
          <motion.div
            key={i}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: i * 0.1 }}
            whileHover={{ scale: 1.03 }}
            className={`bg-gray-50 rounded-xl p-6 shadow-md text-center hover:shadow-lg transition ${
              // Center the last card if it's the only one in its row
              services.length % 3 === 1 && i === services.length - 1
                ? "lg:col-start-2"
                : ""
            }`}
          >
            <div className="flex justify-center mb-4">{service.icon}</div>
            <h3 className="text-lg font-semibold mb-2">{service.title}</h3>
            <p className="text-gray-600 text-sm mb-4">{service.description}</p>
            <a
              href={service.link}
              className="mt-auto inline-block px-4 py-2 text-sm font-semibold text-white bg-cyan-700 rounded hover:bg-cyan-800 transition"
            >
              Learn More
            </a>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
