// Services.jsx
import { FaFileInvoiceDollar, FaBalanceScale, FaClipboardCheck, FaBookOpen, FaCalculator, FaChartLine, FaLightbulb } from "react-icons/fa";

const services = [
  {
    title: "Tax Services",
    description:
      "Tailored tax services for individuals, businesses, and not-for-profits. We ensure full compliance and optimized outcomes.",
    icon: <FaFileInvoiceDollar className="text-3xl text-cyan-600 mb-4" />
  },
  {
    title: "Audit & Attest",
    description:
      "Reliable audits for profit and not-for-profit organizations. We ensure financial accuracy and regulatory compliance.",
    icon: <FaBalanceScale className="text-3xl text-cyan-600 mb-4" />
  },
  {
    title: "Compilations & AUP",
    description:
      "Professional compilations and agreed-upon procedures to deliver precise and trustworthy financial insights.",
    icon: <FaClipboardCheck className="text-3xl text-cyan-600 mb-4" />
  },
  {
    title: "Bookkeeping & Accounting",
    description:
      "Day-to-day bookkeeping and advanced accounting services. We specialize in QuickBooks and financial clarity.",
    icon: <FaBookOpen className="text-3xl text-cyan-600 mb-4" />
  },
  {
    title: "Tax Planning & Preparation",
    description:
      "Modern tax strategies to reduce your liability and keep your filings compliant at all times.",
    icon: <FaCalculator className="text-3xl text-cyan-600 mb-4" />
  },
  {
    title: "Business Accounting",
    description:
      "Comprehensive accounting and payroll support for small businesses with GAAP-compliant reporting.",
    icon: <FaChartLine className="text-3xl text-cyan-600 mb-4" />
  },
  {
    title: "Consulting & Advisory",
    description:
      "Expert guidance in entity setup, scaling strategy, and risk management tailored for your business goals.",
    icon: <FaLightbulb className="text-3xl text-cyan-600 mb-4" />
  }
];

export default function Services() {
  return (
    <section className="py-20 bg-white px-6">
      <h2 className="text-3xl font-bold text-center mb-12">Our Services</h2>
      <div className="grid gap-8 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 max-w-7xl mx-auto">
        {services.map((service, i) => (
          <div key={i} className="bg-gray-50 rounded-xl p-6 shadow-md text-center hover:shadow-lg transition">
            <div className="flex justify-center">{service.icon}</div>
            <h3 className="text-lg font-semibold mb-2 mt-2">{service.title}</h3>
            <p className="text-gray-600 text-sm">{service.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
