const teamMembers = [
  { name: "Avery Chan", position: "Managing Partner" },
  { name: "Leo Ramirez", position: "Tax Compliance Specialist" },
  { name: "Mei Tan", position: "Senior Auditor" },
  { name: "Carlos Rivera", position: "Business Development Lead" },
  { name: "Isabelle Cruz", position: "Accounting Supervisor" },
  { name: "Nathan Yu", position: "Financial Analyst" }
];

export default function Team() {
  return (
    <section className="py-20 px-6 bg-gray-50">
      <h2 className="text-4xl font-bold text-center mb-12">Meet Our Team</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10 max-w-7xl mx-auto">
        {teamMembers.map((member, i) => (
          <div
            key={i}
            className="bg-white p-8 rounded-2xl shadow-lg text-center hover:shadow-xl transition duration-300"
          >
            <img
              src={`https://source.unsplash.com/200x200/?portrait,person&sig=${i}`}
              alt={member.name}
              className="w-32 h-32 mx-auto rounded-full object-cover mb-6"
            />
            <h3 className="text-xl font-semibold">{member.name}</h3>
            <p className="text-gray-600">{member.position}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
