const services = [
  {
    title: "Tax Services (Individuals, Businesses, and Not-for-Profit Organizations)",
    description:
      "Our firm offers comprehensive US tax services tailored to individuals, business entities, and not-for-profits. We ensure compliance and financial optimization through personalized solutions delivered by experienced professionals."
  },
  {
    title: "Audit and Attest Services",
    description:
      "We provide audit and attest services for both profit and not-for-profit organizations. Our skilled auditors ensure accuracy, compliance, and transparency across financial statements with integrity and excellence."
  },
  {
    title: "Compilations and Agreed Upon Procedures",
    description:
      "We offer compilations and agreed-upon procedures to meet specific financial reporting needs. Our team ensures precise, organized, and high-quality services to address targeted financial concerns."
  },
  {
    title: "Bookkeeping and Accounting Services",
    description:
      "We deliver US bookkeeping and accounting services to maintain accurate records and support financial goals. Our team is highly skilled in QuickBooks for efficient financial management and reporting."
  },
  {
    title: "Tax Planning & Preparation",
    description:
      "Comprehensive, up-to-date strategies to minimize your federal and state tax liability while maintaining compliance."
  },
  {
    title: "Business Accounting",
    description:
      "Ongoing bookkeeping, payroll, and GAAP-compliant financial reporting for small businesses and corporations."
  },
  {
    title: "Consulting & Advisory",
    description:
      "Guidance on business formation, strategic growth, financial analysis, and risk management by CPAs."
  }
];

export default function Services() {
  return (
    <section className="py-20 bg-white px-6">
      <h2 className="text-3xl font-bold text-center mb-12">Our Services</h2>
      <div className="grid gap-8 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 max-w-6xl mx-auto">
        {services.map((service, i) => (
          <div key={i} className="bg-gray-100 rounded-lg p-6 shadow-md">
            <h3 className="text-lg font-semibold mb-2">{service.title}</h3>
            <p className="text-sm text-gray-700">{service.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
