import { useState } from "react";

const teamMembers = [
  {
    name: "Avery Chan",
    position: "Managing Partner",
    description:
      "Avery leads our firm with over 15 years of experience in tax strategy, audit, and financial planning.",
    image:
      "https://images.unsplash.com/photo-1607746882042-944635dfe10e?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Leo Ramirez",
    position: "Tax Compliance Specialist",
    description:
      "Leo ensures our clients meet all tax obligations while maximizing savings opportunities.",
    image:
      "https://images.unsplash.com/photo-1603415526960-f7e0328e8328?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Isabelle Cruz",
    position: "Accounting Supervisor",
    description:
      "With a focus on integrity and accuracy, Isabelle oversees all bookkeeping and accounting operations.",
    image:
      "https://images.unsplash.com/photo-1522071820081-009f0129c71c?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Nathan Yu",
    position: "Financial Analyst",
    description:
      "Nathan specializes in helping clients interpret financial data and make informed business decisions.",
    image:
      "https://images.unsplash.com/photo-1603415527081-26d5b6bbf5a9?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Mei Tan",
    position: "Senior Auditor",
    description:
      "Mei conducts in-depth audits and ensures that client records are accurate and compliant.",
    image:
      "https://images.unsplash.com/photo-1524504388940-b1c1722653e1?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Carlos Rivera",
    position: "Business Development Lead",
    description:
      "Carlos drives growth by building client relationships and identifying new market opportunities.",
    image:
      "https://images.unsplash.com/photo-1614283327064-3a2e417f38af?auto=format&fit=crop&w=600&q=80",
  },
];

export default function Team() {
  const [selectedMember, setSelectedMember] = useState(null);

  return (
    <section className="py-20 px-4 bg-gray-50">
      <h2 className="text-4xl font-bold text-center mb-16">Meet Our Team</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10 max-w-7xl mx-auto">
        {teamMembers.map((member, i) => (
          <div
            key={i}
            onClick={() => setSelectedMember(member)}
            className="cursor-pointer group relative overflow-hidden rounded-2xl shadow-lg hover:shadow-2xl transition-shadow bg-white"
          >
            <img
              src={member.image}
              alt={member.name}
              className="w-full h-80 object-cover group-hover:scale-105 transition-transform duration-300"
            />
            <div className="p-6 text-center">
              <h3 className="text-xl font-bold mb-1 text-gray-800 group-hover:text-cyan-700">
                {member.name}
              </h3>
              <p className="text-gray-600 text-sm font-medium">{member.position}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {selectedMember && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl shadow-lg max-w-lg w-full p-8 relative">
            <button
              onClick={() => setSelectedMember(null)}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800"
            >
              &times;
            </button>
            <img
              src={selectedMember.image}
              alt={selectedMember.name}
              className="w-32 h-32 object-cover rounded-full mx-auto mb-4"
            />
            <h3 className="text-2xl font-bold text-center text-cyan-700">
              {selectedMember.name}
            </h3>
            <p className="text-center font-medium text-gray-600 mb-4">
              {selectedMember.position}
            </p>
            <p className="text-gray-700 text-center">{selectedMember.description}</p>
          </div>
        </div>
      )}
    </section>
  );
}
